}; fn main() -> Result<(), Box<dyn std::error::Error>> { // startup: Enable raw mode for the terminal, giving us fine control over user input crossterm::terminal::enable_raw_mode()?; crossterm::execute!(std::io::stderr(), crossterm::terminal::EnterAlternateScreen)?; // Initialize the terminal backend using crossterm let mut terminal = Terminal::new(CrosstermBackend::new(std::io::stderr()))?; // Define our counter variable // This is the state of our application let mut counter = 0; // Main application loop loop { // Render the UI terminal.draw(|f| { f.render_widget(Paragraph::new(format!("Counter: {counter}")), f.size()); })?; // Check for user input every 250 milliseconds if crossterm::event::poll(std::time::Duration::from_millis(250))? { // If a key event occurs, handle it if let crossterm::event::Event::Key(key) = crossterm::event::read()? { match key.code { crossterm::event::KeyCode::Char('j') => counter += 1, crossterm::event::KeyCode::Char('k') => counter -= 1, crossterm::event::KeyCode::Char('q') => break, _ => (), } } } } // shutdown down: reset terminal back to original state crossterm::execute!(std::io::stderr(), crossterm::terminal::LeaveAlternateScreen)?; crossterm::terminal::disable_raw_mode()?; Ok(())
} In the code above, it is useful to think about various parts of the code as separate pieces of the puzzle. This is useful to help refactor and reorganize your code for larger applications.

