Here you can find the finished project used for the tutorial. You can test this application by yourself, but running cargo run > test.json and double checking the output. Main.rs use std::{error::Error, io}; use crossterm::{ event::{self, DisableMouseCapture, EnableMouseCapture, Event, KeyCode, KeyEventKind}, execute, terminal::{disable_raw_mode, enable_raw_mode, EnterAlternateScreen, LeaveAlternateScreen},
};
use ratatui::{ backend::{Backend, CrosstermBackend}, Terminal,
}; mod app;
mod ui;
use crate::{ app::{App, CurrentScreen, CurrentlyEditing}, ui::ui,
}; fn main() -> Result<(), Box<dyn Error>> { // setup terminal enable_raw_mode()?; let mut stderr = io::stderr(); // This is a special case. Normally using stdout is fine execute!(stderr, EnterAlternateScreen, EnableMouseCapture)?; let backend = CrosstermBackend::new(stderr); let mut terminal = Terminal::new(backend)?; // create app and run it let mut app = App::new(); let res = run_app(&mut terminal, &mut app); // restore terminal disable_raw_mode()?; execute!( terminal.backend_mut(), LeaveAlternateScreen, DisableMouseCapture )?; terminal.show_cursor()?; if let Ok(do_print) = res { if do_print { app.print_json()?; } } else if let Err(err) = res { println!("{err:?}"); } Ok(())
} fn run_app<B: Backend>(terminal: &mut Terminal<B>, app: &mut App) -> io::Result<bool> { loop { terminal.draw(|f| ui(f, app))?; if let Event::Key(key) = event::read()? { match app.current_screen { CurrentScreen::Main => match key.code { KeyCode::Char('e') => { app.current_screen = CurrentScreen::Editing; app.currently_editing = Some(CurrentlyEditing::Key); } KeyCode::Char('q') => { app.current_screen = CurrentScreen::Exiting; } _ => {} }, CurrentScreen::Exiting => match key.code { KeyCode::Char('y') => { return Ok(true); } KeyCode::Char('n') | KeyCode::Char('q') => { return Ok(false); } _ => {} }, CurrentScreen::Editing if key.kind == KeyEventKind::Press => { match key.code { KeyCode::Enter => { if let Some(editing) = &app.currently_editing { match editing { CurrentlyEditing::Key => { app.currently_editing = Some(CurrentlyEditing::Value); } CurrentlyEditing::Value => { app.save_key_value(); app.current_screen = CurrentScreen::Main; } } } } KeyCode::Backspace => { if let Some(editing) = &app.currently_editing { match editing { CurrentlyEditing::Key => { app.key_input.pop(); } CurrentlyEditing::Value => { app.value_input.pop(); } } } } KeyCode::Esc => { app.current_screen = CurrentScreen::Main; app.currently_editing = None; } KeyCode::Tab => { app.toggle_editing(); } KeyCode::Char(value) => { if let Some(editing) = &app.currently_editing { match editing { CurrentlyEditing::Key => { app.key_input.push(value); } CurrentlyEditing::Value => { app.value_input.push(value); } } } } _ => {} } } _ => {} } } }
} App.rs use serde_json::Result; pub enum CurrentScreen { Main, Editing, Exiting,
} pub enum CurrentlyEditing { Key, Value,
} pub struct App { pub key_input: String, // the currently being edited json key. pub value_input: String, // the currently being edited json value. pub pairs: HashMap<String, String>, // The representation of our key and value pairs with serde Serialize support pub current_screen: CurrentScreen, // the current screen the user is looking at, and will later determine what is rendered. pub currently_editing: Option<CurrentlyEditing>, // the optional state containing which of the key or value pair the user is editing. It is an option, because when the user is not directly editing a key-value pair, this will be set to `None`.
} impl App { pub fn new() -> App { App { key_input: String::new(), value_input: String::new(), pairs: HashMap::new(), current_screen: CurrentScreen::Main, currently_editing: None, } } pub fn save_key_value(&mut self) { self.pairs .insert(self.key_input.clone(), self.value_input.clone()); self.key_input = String::new(); self.value_input = String::new(); self.currently_editing = None; } pub fn toggle_editing(&mut self) { if let Some(edit_mode) = &self.currently_editing { match edit_mode { CurrentlyEditing::Key => self.currently_editing = Some(CurrentlyEditing::Value), CurrentlyEditing::Value => self.currently_editing = Some(CurrentlyEditing::Key), }; } else { self.currently_editing = Some(CurrentlyEditing::Key); } } pub fn print_json(&self) -> Result<()> { let output = serde_json::to_string(&self.pairs)?; println!("{}", output); Ok(()) }
