}; use crate::app::{App, CurrentScreen, CurrentlyEditing}; pub fn ui<B: Backend>(f: &mut Frame<B>, app: &App) { // Create the layout sections. let chunks = Layout::default() .direction(Direction::Vertical) .constraints( [ Constraint::Length(3), Constraint::Min(1), Constraint::Length(3), ] .as_ref(), ) .split(f.size()); let title_block = Block::default() .borders(Borders::ALL) .style(Style::default()); let title = Paragraph::new(Text::styled( "Create New Json", Style::default().fg(Color::Green), )) .block(title_block); f.render_widget(title, chunks[0]); let mut list_items = Vec::<ListItem>::new(); for key in app.pairs.keys() { list_items.push(ListItem::new(Line::from(Span::styled( format!("{: <25} : {}", key, app.pairs.get(key).unwrap()), Style::default().fg(Color::Yellow), )))); } let list = List::new(list_items); f.render_widget(list, chunks[1]); let current_navigation_text = vec![ // The first half of the text match app.current_screen { CurrentScreen::Main => Span::styled("Normal Mode", Style::default().fg(Color::Green)), CurrentScreen::Editing => { Span::styled("Editing Mode", Style::default().fg(Color::Yellow)) } CurrentScreen::Exiting => Span::styled("Exiting", Style::default().fg(Color::LightRed)), } .to_owned(), // A white divider bar to separate the two sections Span::styled(" | ", Style::default().fg(Color::White)), // The final section of the text, with hints on what the user is editing { if let Some(editing) = &app.currently_editing { match editing { CurrentlyEditing::Key => { Span::styled("Editing Json Key", Style::default().fg(Color::Green)) } CurrentlyEditing::Value => { Span::styled("Editing Json Value", Style::default().fg(Color::LightGreen)) } } } else { Span::styled("Not Editing Anything", Style::default().fg(Color::DarkGray)) } }, ]; let mode_footer = Paragraph::new(Line::from(current_navigation_text)) .block(Block::default().borders(Borders::ALL)); let current_keys_hint = { match app.current_screen { CurrentScreen::Main => Span::styled( "(q) to q