Here’s the dependencies: [package]
name = "ratatui-stopwatch-app"
version = "0.1.0"
edition = "2021" # See more keys and their definitions at https://doc.rust-lang.org/cargo/reference/manifest.html [dependencies]
color-eyre = "0.6.2"
crossterm = { version = "0.27.0", features = ["event-stream"] }
futures = "0.3.28"
itertools = "0.11.0"
log = "0.4.20"
ratatui = "0.23.0"
strum = "0.25.0"
tokio = { version = "1.32.0", features = ["full"] }
tokio-util = "0.7.8"
tui-big-text = "0.1.4" Here’s a gif of what it will look like if you run this: Stopwatch Here’s the full application: use std::time::{Duration, Instant}; use color_eyre::eyre::{eyre, Result};
use futures::{FutureExt, StreamExt};
use itertools::Itertools;
use ratatui::{backend::CrosstermBackend as Backend, prelude::*, widgets::*};
use strum::EnumIs;
use tui_big_text::BigText;
pub type Frame<'a> = ratatui::Frame<'a, Backend<std::io::Stderr>>; #[tokio::main]
async fn main() -> Result<()> { let mut app = StopwatchApp::default(); app.run().await
} #[derive(Clone, Debug)]
pub enum Event { Quit, Error, Tick, Key(crossterm::event::KeyEvent),
} #[derive(Debug, Default, Clone, Copy, PartialEq, Eq, EnumIs)]
enum AppState { #[default] Stopped, Running, Quitting,
} #[derive(Debug, Clone, Copy, PartialEq, Eq)]
enum Message { StartOrSplit, Stop, Tick, Quit,
} #[derive(Debug, Default, Clone, PartialEq)]
struct StopwatchApp { state: AppState, splits: Vec<Instant>, fps_counter: FpsCounter,
} impl StopwatchApp { async fn run(&mut self) -> Result<()> { let mut tui = Tui::new()?; tui.enter()?; tui.start(); while !self.state.is_quitting() { tui.draw(|f| self.draw(f).expect("Unexpected error during drawing"))?; let event = tui.next().await.ok_or(eyre!("Unable to get event"))?; let message = self.handle_event(event)?; self.handle_message(message)?; } tui.exit()?; tui.stop()?; Ok(()) } fn handle_event(&self, event: Event) -> Result<Message> { let msg = match event { Event::Key(key) => { match key.code { crossterm::event::KeyCode::Char('q') => Message::Quit, crossterm::event::KeyCode::Char(' ') => Message::StartOrSplit, crossterm::event::KeyCode::Char('s') | crossterm::event::KeyCode::Enter => Message::Stop, _ => Message::Tick, } }, _ => Message::Tick, }; Ok(msg) } fn handle_message(&mut self, message: Message) -> Result<()> { match message { Message::StartOrSplit => self.start_or_split(), Message::Stop => self.stop(), Message::Tick => self.tick(), Message::Quit => self.quit(), } Ok(()) } fn start_or_split(&mut self) { if self.state.is_stopped() { self.start(); } else { self.record_split(); } } fn stop(&mut self) { self.record_split(); self.state = AppState::Stopped; } fn tick(&mut self) { self.fps_counter.tick() } fn quit(&mut self) { self.state = AppState::Quitting } fn start(&mut self) { self.splits.clear(); self.state = AppState::Running; self.record_split(); } fn record_split(&mut self) { if !self.state.is_running() { return; } self.splits.push(Instant::now()); } fn elapsed(&mut self) -> Duration { if self.state.is_running() { self.splits.first().map_or(Duration::ZERO, Instant::elapsed) } else { // last - first or 0 if there are no splits let now = Instant::now(); let first = *self.splits.first().unwrap_or(&now); let last = *self.splits.last().unwrap_or(&now); last - first } } fn draw(&mut self, f: &mut Frame) -> Result<()> { let layout = layout(f.size()); f.render_widget(Paragraph::new("Stopwatch Example"), layout[0]); f.render_widget(self.fps_paragraph(), layout[1]); f.render_widget(self.timer_paragraph(), layout[2]); f.render_widget(Paragraph::new("Splits:"), layout[3]); f.render_widget(self.splits_paragraph(), layout[4]); f.render_widget(self.help_paragraph(), layout[5]); Ok(()) } fn fps_paragraph(&mut self) -> Paragraph<'_> { let fps = format!("{:.2} fps", self.fps_counter.fps); Paragraph::new(fps).style(Style::new().dim()).alignment(Alignment::Right) } fn timer_paragraph(&mut self) -> BigText<'_> { let style = if self.state.is_running() { Style::new().green() } else { Style::new().red() }; let duration = format_duration(self.elapsed()); let lines = vec![duration.into()]; tui_big_text::BigTextBuilder::default().lines(lines).style(style).build().unwrap() } /// Renders the splits as a list of lines. /// /// ```text /// #01 -- 00:00.693 -- 00:00.693 /// #02 -- 00:00.719 -- 00:01.413 /// ``` fn splits_paragraph(&mut self) -> Paragraph<'_> { let start = *self.splits.first().unwrap_or(&Instant::now()); let mut splits = self .splits .iter() .copied() .tuple_windows() .enumerate() .map(|(index, (prev, current))| format_split(index, start, prev, current)) .collect::<Vec<_>>(); splits.reverse(); Paragraph::new(splits) } fn help_paragraph(&mut self) -> Paragraph<'_> { let space_action = if self.state.is_stopped() { "start" } else { "split" }; let help_text = Line::from(vec!["space ".into(), space_action.dim(), " enter ".into(), "stop".dim(), " q ".into(), "quit".dim()]); Paragraph::new(help_text).gray() }
} fn layout(area: Rect) -> Vec<Rect> { let layout = Layout::default() .direction(Direction::Vertical) .constraints(vec![ Constraint::Length(2), // top bar Constraint::Length(8), // timer Constraint::Length(1), // splits header Constraint::Min(0), // splits Constraint::Length(1), // help ]) .split(area); let top_layout = Layout::default() .direction(Direction::Horizontal) .constraints(vec![ Constraint::Length(20), // title Constraint::Min(0), // fps counter ]) .split(layout[0]); // return a new vec with the top_layout rects and then rest of layout top_layout[..].iter().chain(layout[1..].iter()).copied().collect()
} fn format_split<'a>(index: usize, start: Instant, previous: Instant, current: Instant) -> Line<'a> { let split = format_duration(current - previous); let elapsed = format_duration(current - start); Line::from(vec![ format!("#{:02} -- ", index + 1).into(), Span::styled(split, Style::new().yellow()), " -- ".into(), Span::styled(elapsed, Style::new()), ])
} fn format_duration(duration: Duration) -> String { format!("{:02}:{:02}.{:03}", duration.as_secs() / 60, duration.as_secs() % 60, duration.subsec_millis())
} #[derive(Debug, Clone, PartialEq)]
struct FpsCounter { start_time: Instant, frames: u32, pub fps: f64,
} impl Default for FpsCounter { fn default() -> Self { Self::new() }
} impl FpsCounter { fn new() -> Self { Self { start_time: Instant::now(), frames: 0, fps: 0.0 } } fn tick(&mut self) { self.frames += 1; let now = Instant::now(); let elapsed = (now - self.start_time).as_secs_f64(); if elapsed >= 1.0 { self.fps = self.frames as f64 / elapsed; self.start_time = now; self.frames = 0; } }
} struct Tui { pub terminal: Terminal<Backend<std::io::Stderr>>, pub task: tokio::task::JoinHandle<()>, pub cancellation_token: tokio_util::sync::CancellationToken, pub event_rx: tokio::sync::mpsc::UnboundedReceiver<Event>, pub event_tx: tokio::sync::mpsc::UnboundedSender<Event>,
} impl Tui { fn new() -> Result<Tui> { let terminal = ratatui::Terminal::new(Backend::new(std::io::stderr()))?; let (event_tx, event_rx) = tokio::sync::mpsc::unbounded_channel(); let cancellation_token = tokio_util::sync::CancellationToken::new(); let task = tokio::spawn(async {}); Ok(Self { terminal, task, cancellation_token, event_rx, event_tx }) } pub async fn next(&mut self) -> Option<Event> { self.event_rx.recv().await } pub fn enter(&mut self) -> Result<()> { crossterm::terminal::enable_raw_mode()?; crossterm::execute!(std::io::stderr(), crossterm::terminal::EnterAlternateScreen, crossterm::cursor::Hide)?; self.start(); Ok(()) } pub fn exit(&self) -> Result<()> { crossterm::execute!(std::io::stderr(), crossterm::terminal::LeaveAlternateScreen, crossterm::cursor::Show)?; crossterm::terminal::disable_raw_mode()?; self.cancel(); Ok(()) } pub fn cancel(&self) { self.cancellation_token.cancel(); } pub fn stop(&self) -> Result<()> { self.cancel(); let mut counter = 0; while !self.task.is_finished() { std::thread::sleep(Duration::from_millis(250)); counter += 1; if counter > 5 { self.task.abort(); } if counter > 10 { log::error!("Failed to abort task for unknown reason"); return Err(eyre!("Unable to abort task")); } } Ok(()) } pub fn start(&mut self) { let tick_rate = std::time::Duration::from_millis(60); self.cancel(); self.cancellation_token = tokio_util::sync::CancellationToken::new(); let _cancellation_token = self.cancellation_token.clone(); let _event_tx = self.event_tx.clone(); self.task = tokio::spawn(async move { let mut reader = crossterm::event::EventStream::new(); let mut interval = tokio::time::interval(tick_rate); loop { let delay = interval.tick(); let crossterm_event = reader.next().fuse(); tokio::select! { _ = _cancellation_token.cancelled() => { break; } maybe_event = crossterm_event => { match maybe_event { Some(Ok(evt)) => { match evt { crossterm::event::Event::Key(key) => { if key.kind == crossterm::event::KeyEventKind::Press { _event_tx.send(Event::Key(key)).unwrap(); } }, _ => {} } } Some(Err(_)) => { _event_tx.send(Event::Error).unwrap(); } None => {}, } }, _ = delay => { _event_tx.send(Event::Tick).unwrap(); }, } } }); }
} impl std::ops::Deref for Tui { type Target = ratatui::Terminal<Backend<std::io::Stderr>>; fn deref(&self) -> &Self::Target { &self.terminal }
} impl std::ops::DerefMut for Tui { fn deref_mut(&mut self) -> &mut Self::Target { &mut self.terminal }
} impl Drop for Tui { fn drop(&mut self) { self.exit().unwrap(); }
}

